mapper.select:
select * from class
where teacher_id = #{teacher_id} and class_type = #{class_type}
limit #{pageNum},#{pageSize}

select * from class
where status = #{status}
and class_type = #{class_type}
and class_name like concat('%',#{input},'%')
limit #{pageNum},#{pageSize}

select * from class
where
status = #{status}
status = 1 or status = 3
and class_type = #{class_type}
and class_name like concat('%',#{input},'%')
limit #{pageNum},#{pageSize}

select count(1) from class
where
status = #{status}
status = 1 or status = 3
and class_type = #{class_type}
and class_name like concat('%',#{input},'%')
